                var dateArr = date.split('-');
                var Day = (dateArr[2] <= 9) ? ("0" + dateArr[2]) : dateArr[2];
                Day = Day.slice(-2);
                var Month = (dateArr[1] <= 9) ? ("0" + dateArr[1]) : dateArr[1];
                Month = Month.slice(-2);
                date = Day + "-" + Month + "-" + dateArr[0];               
